<style type="text/css">
	.contact__form {
    	margin-top: 20px;
    	margin-bottom: 20px;
    	padding-top: 10px;
    	padding-bottom: 10px;
    	box-shadow: 10px 0px 40px 0px var(--clr-boxshadow);
	}

	.contact__form .bg-highlight {
		width: 100px;
	    top: 0px;
	    right: 0px;
	    background-color: var(--clr-primary);
	}

	.contact__form form input {
		font-size: 14px;
	    color: var(--clr-text);
	    height: 35px;
	    min-height: 35px;
	    border: 0;
     	outline: 0;
	    border-bottom: 1px solid var(--clr-border-contact);
	}

	.contact__form form input::placeholder {
		color: var(--clr-black);
	}

	.contact__form form button {
		font-size: 16px;
		line-height: 45px;
		height: 46px;
		padding: 0px 12px;
		border: 1px solid var(--clr-black);
	}

	.contact__form form button:hover {
		background-color: var(--clr-primary) !important;
		border-color: var(--clr-primary);
	}

	.contact__infor {
		padding: 16px 40px;
	}

	.contact__infor .point {
	    width: 30px;
		height: 30px;
		top: -15px;
		left: -15px;
		background-color: var(--clr-primary);
	}

	@media (min-width:768px) {
		.contact__form {
			margin-top: 20px;
			margin-bottom: 50px;
			padding: 70px 0px 40px 50px;
		}

		.contact__form .bg-highlight {
			width: 150px;
		}
	}

	@media (min-width: 1400px) {
		.contact__form {
			padding-left: 100px;
		}
	}
</style>

